// src/components/Map.js
import React, { useState } from 'react';
import { GoogleMap, Marker, useLoadScript } from '@react-google-maps/api';
import Popup from './Popup';

const Map = () => {
  const [markerPosition, setMarkerPosition] = useState(null);
  const [showPopup, setShowPopup] = useState(false);

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: 'AIzaSyD1JWD2LMUTfBiF6Gk5jpuMxMpE7_q9EE8', // Replace with your Google Maps API key
  });

  const handleMapClick = (event) => {
    setMarkerPosition({
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
    });
    setShowPopup(true);
  };

  const handleSelectDisaster = (disasterType) => {
    console.log(`User selected: ${disasterType} at`, markerPosition);
    // Send this data to the backend
    setShowPopup(false); // Close popup after selection
  };

  if (!isLoaded) return <div>Loading...</div>;

  return (
    <>
      <GoogleMap
        zoom={12}
        center={markerPosition || { lat: 37.7749, lng: -122.4194 }} // Default to San Francisco
        mapContainerStyle={{ width: '100%', height: '100vh' }}
        onClick={handleMapClick}
      >
        {markerPosition && (
          <Marker
            position={markerPosition}
            draggable={true}
            onDragEnd={handleMapClick}
          />
        )}
      </GoogleMap>
      {showPopup && (
        <Popup
          onSelectDisaster={handleSelectDisaster}
          onClose={() => setShowPopup(false)}
        />
      )}
    </>
  );
};

export default Map;
