// src/components/Map.js
import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, Marker, useLoadScript, Autocomplete } from '@react-google-maps/api';
import Popup from './Popup';

const Map = ({ isCallForHelpMode }) => {
  const [markerPosition, setMarkerPosition] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [isLocationConfirmed, setIsLocationConfirmed] = useState(false);
  const autocompleteRef = useRef(null);

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: 'AIzaSyD1JWD2LMUTfBiF6Gk5jpuMxMpE7_q9EE8', // Replace with your API key
    libraries: ['places'], // Enable Places API
  });

  // Get the user's current location
  useEffect(() => {
    if (isCallForHelpMode && navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setMarkerPosition({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (error) => {
          console.error('Error getting user location:', error);
        }
      );
    }
  }, [isCallForHelpMode]);

  // Handle map clicks (only if location is not confirmed)
  const handleMapClick = (event) => {
    if (isCallForHelpMode && !isLocationConfirmed) {
      setMarkerPosition({
        lat: event.latLng.lat(),
        lng: event.latLng.lng(),
      });
    }
  };

  // Handle address search
  const onPlaceChanged = () => {
    if (autocompleteRef.current) {
      const place = autocompleteRef.current.getPlace();
      if (place.geometry) {
        setMarkerPosition({
          lat: place.geometry.location.lat(),
          lng: place.geometry.location.lng(),
        });
      }
    }
  };

  // Handle disaster selection
  const handleSelectDisaster = (disasterType) => {
    console.log(`User selected: ${disasterType} at`, markerPosition);
    // Send this data to the backend
    setIsLocationConfirmed(true); // Lock the pin
    setShowPopup(false); // Close popup
  };

  if (!isLoaded) return <div>Loading...</div>;

  return (
    <>
      <GoogleMap
        zoom={12}
        center={markerPosition || { lat: 13, lng: 500}} //700 Default to San Francisco
        mapContainerStyle={{ width: '100%', height: '100vh' }}
        onClick={handleMapClick}
      >
        {isCallForHelpMode && markerPosition && (
          <Marker
            position={markerPosition}
            draggable={!isLocationConfirmed} // Disable dragging if location is confirmed
            onDragEnd={(event) =>
              setMarkerPosition({
                lat: event.latLng.lat(),
                lng: event.latLng.lng(),
              })
            }
          />
        )}
      </GoogleMap>

      {/* Search Bar */}
      {isCallForHelpMode && !isLocationConfirmed && (
        <div style={{ position: 'absolute', top: '20px', left: '220px', zIndex: 1000 }}>
          <Autocomplete
            onLoad={(autocomplete) => (autocompleteRef.current = autocomplete)}
            onPlaceChanged={onPlaceChanged}
          >
            <input
              type="text"
              placeholder="Search for an address"
              style={{ width: '300px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
            />
          </Autocomplete>
        </div>
      )}

      {/* Confirm Location Button */}
      {isCallForHelpMode && !isLocationConfirmed && (
        <button
          onClick={() => setShowPopup(true)}
          style={{
            position: 'absolute',
            top: '70px',
            left: '220px',
            zIndex: 1000,
            padding: '10px 20px',
            backgroundColor: '#4CAF50',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Confirm Location
        </button>
      )}

      {/* Popup */}
      {showPopup && (
        <Popup
          onSelectDisaster={handleSelectDisaster}
          onClose={() => setShowPopup(false)}
        />
      )}
    </>
  );
};

export default Map;
