// src/App.js
import React, { useState } from 'react';
import StartingPage from './components/StartingPage';
import Navbar from './components/Navbar';
import Map from './components/Map';
import './App.css';

function App() {
  const [isCallForHelpMode, setIsCallForHelpMode] = useState(false);
  const [role, setRole] = useState(null); // Track the selected role (victim or responder)
  const [userDetails, setUserDetails] = useState({ name: '', contactNumber: '' }); // Store user details

  // Handle role selection
  const handleSelectRole = (selectedRole) => {
    setRole(selectedRole);
  };

  // Handle back button click
  const handleBack = () => {
    setRole(null); // Reset the role to return to the starting page
  };

  // Handle saving user details
  const handleSaveUserDetails = (name, contactNumber) => {
    setUserDetails({ name, contactNumber });
  };

  // Render the appropriate component based on the selected role
  const renderContent = () => {
    if (!role) {
      return <StartingPage onSelectRole={handleSelectRole} />;
    } else if (role === 'victim') {
      return (
        <>
          <Navbar
            onCallForHelp={() => setIsCallForHelpMode(true)}
            onBack={handleBack}
            onSaveUserDetails={handleSaveUserDetails}
          />
          <Map
            isCallForHelpMode={isCallForHelpMode}
            setIsCallForHelpMode={setIsCallForHelpMode}
          />
        </>
      );
    } else if (role === 'responder') {
      return (
        <>
          <Navbar onBack={handleBack} />
          <div className="responder-placeholder">
            <h3>Responder View</h3>
            <p>Name: {userDetails.name}</p>
            <p>Contact Number: {userDetails.contactNumber}</p>
          </div>
        </>
      );
    }
  };

  return (
    <div style={{ display: 'flex', height: '100vh', overflow: 'hidden' }}>
      {renderContent()}
    </div>
  );
}

export default App;
