// src/components/Navbar.js
import React, { useState } from 'react';
import './Navbar.css';

const Navbar = ({ onCallForHelp, onBack, onSaveUserDetails }) => {
  const [showAccountPopup, setShowAccountPopup] = useState(false);
  const [name, setName] = useState('');
  const [contactNumber, setContactNumber] = useState('');

  // Handle account popup open/close
  const toggleAccountPopup = () => {
    setShowAccountPopup(!showAccountPopup);
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (/^\d+$/.test(contactNumber)) { // Validate contact number is numeric
      onSaveUserDetails(name, contactNumber); // Save user details
      setShowAccountPopup(false); // Close the popup
    } else {
      alert('Please enter a valid contact number (numbers only).');
    }
  };

  return (
    <div className="nav-bar">
      <h2>PWET NI ALBERT</h2>
      <button className="nav-button" onClick={onCallForHelp}>
        Call for Help
      </button>
      <button className="back-button" onClick={onBack}>
        Back
      </button>
      <button className="account-button" onClick={toggleAccountPopup}>
        Account
      </button>

      {/* Account Popup */}
      {showAccountPopup && (
        <div className="account-popup">
          <div className="popup-content">
            <span className="close-icon" onClick={toggleAccountPopup}>
              &times;
            </span>
            <h3>Enter Your Details</h3>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              <input
                type="tel"
                placeholder="Contact Number"
                value={contactNumber}
                onChange={(e) => setContactNumber(e.target.value)}
                pattern="\d*" // Ensure only numbers are accepted
                required
              />
              <button type="submit">Save</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Navbar;
