// src/App.js
import React, { useState } from 'react';
import StartingPage from './components/StartingPage';
import Navbar from './components/Navbar';
import Map from './components/Map';
import './App.css';

function App() {
  const [isCallForHelpMode, setIsCallForHelpMode] = useState(false);
  const [role, setRole] = useState(null); // Track the selected role (victim or responder)
  const [showStartingPage, setShowStartingPage] = useState(true); // Control visibility of starting page

  // Handle role selection
  const handleSelectRole = (selectedRole) => {
    setRole(selectedRole);
    // Start the transition
    setShowStartingPage(false);
  };

  // Render the appropriate component based on the selected role
  const renderContent = () => {
    if (role === 'victim') {
      return (
        <>
          <Navbar onCallForHelp={() => setIsCallForHelpMode(true)} />
          <Map
            isCallForHelpMode={isCallForHelpMode}
            setIsCallForHelpMode={setIsCallForHelpMode}
          />
        </>
      );
    } else if (role === 'responder') {
      return <div className="responder-placeholder">Responder view not implemented yet.</div>;
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: role === 'victim' ? 'row' : 'column' }}>
      {/* Starting Page with Transition */}
      <div className={`starting-page-container ${showStartingPage ? 'visible' : 'hidden'}`}>
        <StartingPage onSelectRole={handleSelectRole} />
      </div>

      {/* Main App with Transition */}
      <div className={`app-container ${!showStartingPage ? 'visible' : 'hidden'}`}>
        {renderContent()}
      </div>
    </div>
  );
}

export default App;
